mapper.select:
SELECT * FROM ( SELECT A.REDUCE_CD || A.CUST_CD AS SEL_KEY ,A.REDUCE_CD ,A.CUST_CD ,A.UNIT_WEIGHT ,A.REDUCE_DESC ,'전체' AS CUST_NM FROM REDUCE_CD_DETAIL A WHERE A.CUST_CD = 'ALL'
UNION
SELECT A.REDUCE_CD || A.CUST_CD AS SEL_KEY ,A.REDUCE_CD ,A.CUST_CD ,A.UNIT_WEIGHT ,A.REDUCE_DESC ,CASE WHEN A.CUST_CD = 'ALL' THEN '전체' WHEN B.CUST_CD IS NOT NULL THEN B.CUST_NM ELSE '' END AS CUST_NM FROM WSSDBO.REDUCE_CD_DETAIL A LEFT JOIN KOSCO.BBAT041 B ON A.CUST_CD = B.CUST_CD WHERE A.CUST_CD <> 'ALL'
AND A.CUST_CD = #{custCd}
AND B.CUST_NM = #{custNm}
) T ORDER BY T.REDUCE_CD
