mapper.select:
SELECT A.SG ,SUBSTR(A.DT, 0, 4) || '-' || SUBSTR(A.DT, 5, 2) || '-' || SUBSTR(A.DT, 7, 2) AS DT ,SUBSTR(A.DT, 0, 4) || '-' || SUBSTR(A.DT, 5, 2) || '-' || SUBSTR(A.DT, 7, 2) AS ORG_DT ,A.SEQ ,A.CATEGORY ,( SELECT CD_NM_KOR FROM WSSDBO.TB_CD_DETAIL WHERE MAST_CD = 'CATEGORY_GBN_CD' AND DETL_CD = A.CATEGORY ) AS CATEGORY_NM ,A.TURN ,A.IO_GB ,CASE WHEN A.IO_GB = '1' THEN '매입' WHEN A.IO_GB = '2' THEN '매출' ELSE '' END AS IO_GB_NM ,A.ST_GB ,A.ETM ,A.OTM ,A.CAR_NO ,A.CAR_CD ,A.CUST_CD ,A.CUST_NM ,A.DRV_NM ,REGEXP_REPLACE(REPLACE(A.DRV_TEL, '-', ''), '(.{3})(.+)(.{4})', '\1-\2-\3') AS DRV_TEL ,A.PROD_NM ,A.SAL_DT ,A.SAL_JNO ,A.PROD_GB ,A.PROD_CD ,A.PROD_CD1 ,A.GRADE ,A.GRADE_NM ,A.AREA_CD ,B.AREA_NM ,A.VENDOR_CD ,C.VENDOR_NM ,A.DECREASE_CODE ,A.DECREASE_WGT ,CASE WHEN ( SELECT COUNT(AA.REDUCE_CD) FROM WSSDBO.REDUCE_CD_DETAIL AA WHERE REDUCE_CD = A.DECREASE_CODE ) = 1 THEN ( SELECT AA.REDUCE_DESC || '-' || CASE WHEN AA.CUST_CD = 'ALL' THEN '전체' WHEN BB.CUST_CD IS NOT NULL THEN BB.CUST_NM ELSE '' END AS REDUCE_DESC FROM WSSDBO.REDUCE_CD_DETAIL AA LEFT JOIN KOSCO.BBAT041 BB ON AA.CUST_CD = BB.CUST_CD WHERE AA.REDUCE_CD = A.DECREASE_CODE ) WHEN ( SELECT COUNT(AA.REDUCE_CD) FROM WSSDBO.REDUCE_CD_DETAIL AA WHERE REDUCE_CD = A.DECREASE_CODE ) > 1 AND A.CUST_NM IS NULL THEN ( SELECT AA.REDUCE_DESC || '-' || CASE WHEN AA.CUST_CD = 'ALL' THEN '전체' WHEN BB.CUST_CD IS NOT NULL THEN BB.CUST_NM ELSE '' END AS REDUCE_DESC FROM WSSDBO.REDUCE_CD_DETAIL AA LEFT JOIN KOSCO.BBAT041 BB ON AA.CUST_CD = BB.CUST_CD WHERE AA.REDUCE_CD = A.DECREASE_CODE AND AA.CUST_CD = 'ALL' AND ROWNUM = 1 ) WHEN ( SELECT COUNT(AA.REDUCE_CD) FROM WSSDBO.REDUCE_CD_DETAIL AA WHERE REDUCE_CD = A.DECREASE_CODE ) > 1 AND A.CUST_NM IS NOT NULL THEN ( CASE WHEN ( SELECT COUNT(A1.REDUCE_CD) FROM WSSDBO.REDUCE_CD_DETAIL A1 LEFT JOIN KOSCO.BBAT041 B1 ON A1.CUST_CD = B1.CUST_CD WHERE A1.REDUCE_CD = A.DECREASE_CODE AND B1.CUST_NM = A.CUST_NM ) = 0 THEN ( SELECT AA.REDUCE_DESC || '-' || CASE WHEN AA.CUST_CD = 'ALL' THEN '전체' WHEN BB.CUST_CD IS NOT NULL THEN BB.CUST_NM ELSE '' END AS REDUCE_DESC FROM WSSDBO.REDUCE_CD_DETAIL AA LEFT JOIN KOSCO.BBAT041 BB ON AA.CUST_CD = BB.CUST_CD WHERE AA.REDUCE_CD = A.DECREASE_CODE AND AA.CUST_CD = 'ALL' AND ROWNUM = 1 ) ELSE ( SELECT AA.REDUCE_DESC || '-' || CASE WHEN AA.CUST_CD = 'ALL' THEN '전체' WHEN BB.CUST_CD IS NOT NULL THEN BB.CUST_NM ELSE '' END AS REDUCE_DESC FROM WSSDBO.REDUCE_CD_DETAIL AA LEFT JOIN KOSCO.BBAT041 BB ON AA.CUST_CD = BB.CUST_CD WHERE AA.REDUCE_CD = A.DECREASE_CODE AND BB.CUST_NM = A.CUST_NM ) END ) ELSE '' END AS REDUCE_DESC ,A.CAR_TYPE ,D.CODE_NAME AS CAR_TYPE_NM ,A.ADD_CD ,A.MIX_CD ,A.PT_COUNT ,A.BANPUM_YN ,CASE WHEN A.BANPUM_YN = 'Y' THEN 'true' ELSE 'false' END AS BANPUM_CHK ,A.COT_NUM ,A.REF_SG ,A.REF_DT ,A.REF_SEQ ,A.BIGO ,A.IN_GB AS SAVE_GB ,CASE WHEN A.IN_GB = '1' THEN '사전배차' WHEN A.IN_GB = '2' THEN '긴급배차' ELSE '구분값 미등록' END AS IN_GB_NM ,STATUS_CD ,( SELECT CD_NM_KOR FROM TB_CD_DETAIL WHERE MAST_CD = 'TRANS_STATUS_CD' AND DETL_CD = A.STATUS_CD ) AS STATUS ,A.IS_NO ,A.IS_DT ,A.UP_NO ,A.UP_DT ,CASE WHEN A.IO_GB = 1 THEN H.TWGT ELSE H.EWGT END IN_WEIGHT ,CASE WHEN A.IO_GB = 1 THEN H.EWGT ELSE H.TWGT END OUT_WEIGHT ,F.CAR_WEIGHT -- ,F.REAL_WEIGHT ,ABS(H.WGT) AS REAL_WEIGHT ,H.MWGT FROM WSSDBO.BWAT011 A LEFT JOIN ( SELECT B.AREA_CD, A.CODE_NAME||' '||B.AREA_NM AS AREA_NM, B.AREA_CD AS ATTR1 FROM (SELECT CODE,CODE_NAME FROM KOSCO.BCOD011 WHERE GUBUN = '4062' AND USE_GUBUN = '1') A, (SELECT AREA_CD, AREA_NM, AREA_GB FROM KOSCO.BSAT020 WHERE USE_YN = 'Y' AND AREA_CD NOT IN ('F13','I43','K25','K24','H47','H46','B14','B11','B13','N47','H49','H41','N48','C12','C13','H42','H45','H50','I40','I41','I42','H51','N46') ) B WHERE A.CODE = B.AREA_GB ) B ON A.AREA_CD = B.AREA_CD LEFT JOIN KOSCO.BBAT051 C ON A.VENDOR_CD = C.VENDOR_CD LEFT JOIN KOSCO.BCOD011 D ON A.CAR_TYPE = D.CODE AND D.GUBUN = 'H003' LEFT JOIN WSSDBO.WSS_RECORD_DATA E ON A.SG = E.SG AND A.DT = E.DT AND A.SEQ = E.SEQ AND E.WBRDG_SET_IO = 'IN' LEFT JOIN WSSDBO.WSS_RECORD_DATA F ON A.SG = F.SG AND A.DT = F.DT AND A.SEQ = F.SEQ AND F.WBRDG_SET_IO = 'OUT' LEFT JOIN KOSCO.BWAT010 H ON A.REF_SG = H.SG AND A.REF_DT = H.DT AND A.REF_SEQ = H.SEQ WHERE A.CATEGORY = '4'
AND A.DT = REPLACE(#{dt}, '-')
AND A.DT BETWEEN NVL(REPLACE(#{dt}, '-'), TO_CHAR(TRUNC(SYSDATE - 7, 'DD'), 'YYYYMMDD')) AND NVL(REPLACE(#{dt}, '-'), TO_CHAR(TRUNC(SYSDATE + 7, 'DD'), 'YYYYMMDD'))
AND A.CAR_CD = #{custCd}
AND A.PROD_CD1 = #{prodCd1}
AND A.PROD_NM LIKE '%' || #{prodNm} || '%'
AND A.STATUS_CD = #{status}
AND A.CAR_NO LIKE '%' || #{carNo} || '%'
AND A.IN_GB = #{saveGB}
AND A.IO_GB = #{ioGb}
ORDER BY A.DT, A.CAR_NO, A.TURN

mapper.insert:
INSERT INTO WSSDBO.BWAT011
(
     SG
    ,DT
    ,SEQ
    ,CATEGORY
    ,TURN
    ,IO_GB
    ,ST_GB
    ,ETM
    ,OTM
    ,CAR_NO
    ,CAR_CD
    ,CUST_CD
    ,CUST_NM
    ,DRV_NM
    ,DRV_TEL
    ,PROD_NM
    ,SAL_DT
    ,SAL_JNO
    ,PROD_GB
    ,PROD_CD
    ,PROD_CD1
    ,GRADE
    ,GRADE_NM
    ,AREA_CD
    ,VENDOR_CD
    ,DECREASE_CODE
    ,DECREASE_WGT
    ,CAR_TYPE
    ,ADD_CD
    ,MIX_CD
    ,PT_COUNT
    ,BANPUM_YN
    ,COT_NUM
    ,REF_SG
    ,REF_DT
    ,REF_SEQ
    ,BIGO
    ,IN_GB
    ,STATUS_CD
    ,IS_NO
    ,IS_DT
)
VALUES
(
     #{sg}
    ,REPLACE(REPLACE(#{dt}, '-'), ' ', '')
    ,(SELECT NVL(MAX(SEQ), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-'))
    ,#{category}
    ,(SELECT NVL(MAX(TURN), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-') AND CAR_NO = #{carNo})
    ,#{ioGb}
    ,#{stGb}
    ,#{etm}
    ,#{otm}
    ,#{carNo}
    ,#{carCd}
    ,null
    ,#{custNm}
    ,#{drvNm}
    ,REPLACE(REPLACE(#{drvTel}, '-'), ' ', '')
    ,#{prodNm}
    ,#{salDt}
    ,#{salJno}
    ,#{prodGb}
    ,#{prodCd}
    ,#{prodCd1}
    ,null
    ,#{gradeNm}
    ,#{areaCd}
    ,#{vendorCd}
    ,#{decreaseCode}
    ,#{decreaseWgt}
    ,#{carType}
    ,#{addCd}
    ,#{mixCd}
    ,#{ptCount}
    ,#{banpumYn}
    ,#{cotNum}
    ,#{refSg}
    ,#{refDt}
    ,#{refSeq}
    ,#{bigo}
    ,'2'
    ,'100'
    ,#{isNo}
    ,SYSDATE
)

mapper.update:
    UPDATE WSSDBO.BWAT011
       SET DT            = REPLACE(#{dt}, '-')
          ,CATEGORY      = #{category}
          ,IO_GB         = #{ioGb}
          ,CAR_NO        = #{carNo}
          ,CAR_CD        = #{carCd}

          ,CUST_NM       = #{custNm}
          ,DRV_NM        = #{drvNm}
          ,DRV_TEL       = REPLACE(REPLACE(#{drvTel}, '-'), ' ', '')
          ,PROD_NM       = #{prodNm}
          ,PROD_CD       = #{prodCd}
          ,PROD_CD1      = #{prodCd1}
          ,GRADE         = #{grade}
          ,GRADE_NM      = #{gradeNm}
          ,AREA_CD       = #{areaCd}
          ,VENDOR_CD     = #{vendorCd}
          ,DECREASE_CODE = #{decreaseCode}
          ,DECREASE_WGT  = #{decreaseWgt}
          ,CAR_TYPE      = #{carType}
          ,COT_NUM       = #{cotNum}
	      ,BIGO          = #{bigo}
          ,UP_NO         = #{sysUserId}
          ,UP_DT         = sysdate

          ,TURN          = (SELECT NVL(MAX(TURN), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-') AND CAR_NO = #{carNo})
          ,SEQ           = (SELECT NVL(MAX(SEQ), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-'))


          ,TURN          = (SELECT NVL(MAX(TURN), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-') AND CAR_NO = #{carNo})
          ,SEQ           = (SELECT NVL(MAX(SEQ), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-'))


          ,TURN          = (SELECT NVL(MAX(TURN), 0) + 1 FROM WSSDBO.BWAT011 WHERE DT = REPLACE(#{dt}, '-') AND CAR_NO = #{carNo})

          ,BANPUM_YN     = #{banpumYn}
     WHERE SG  = #{sg}
       AND DT  = REPLACE(#{orgDt}, '-')
       AND SEQ = #{seq}
       AND STATUS_CD = '100'

mapper.select:
SELECT DT, CAR_NO, CUST_CD, CAR_CD
  FROM WSSDBO.BWAT011
 WHERE SG = #{sg}
   AND DT = REPLACE(#{orgDt}, '-')
   AND SEQ = #{seq}

mapper.delete:
DELETE FROM WSSDBO.BWAT011
 WHERE SG = #{sg}
   AND DT = REPLACE(#{orgDt}, '-')
   AND SEQ = #{seq}
   AND STATUS_CD = '100'
